CREATE TABLE SEC_USER  (
   ID                   VARCHAR2(38)                    NOT NULL,
   NAME                 VARCHAR2(50)                    NOT NULL,
   TYPE                 VARCHAR2(10)                   DEFAULT 'local' NOT NULL
      CONSTRAINT CKC_TYPE_SEC_USER CHECK (TYPE IN ('local','portal')),
   LOGIN_ID             VARCHAR2(30)                    NOT NULL,
   PASSWORD             VARCHAR2(50),
   EMAIL                VARCHAR2(50),
   MOBILE_PHONE         VARCHAR2(15),
   IM                   VARCHAR2(50),
   TELEPHONE            VARCHAR2(15),
   SEX                  CHAR(1)                        DEFAULT 'U' NOT NULL
      CONSTRAINT CKC_SEX_SEC_USER CHECK (SEX IN ('F','M','U')),
   BIRTHDAY             DATE,
   STATUS               VARCHAR2(10)                   DEFAULT 'enabled' NOT NULL
      CONSTRAINT CKC_STATUS_SEC_USER CHECK (STATUS IN ('enabled','disabled')),
   ORG_ID               VARCHAR2(38)                    NOT NULL,
   RANK                 VARCHAR2(50),
   CREATED_BY           VARCHAR2(38)                    NOT NULL,
   CREATED_DATE         DATE                            NOT NULL,
   LAST_UPDATED_BY      VARCHAR2(38)                    NOT NULL,
   LAST_UPDATED_DATE    DATE                            NOT NULL,
   CONSTRAINT PK_SEC_USER PRIMARY KEY (ID),
   CONSTRAINT AK_AK_USERLOGINID_SEC_USER UNIQUE (LOGIN_ID, TYPE)
);

