COMMENT ON COLUMN SEC_WF_ROLE.CREATED_BY IS
'创建人';

COMMENT ON COLUMN SEC_WF_ROLE.CREATED_DATE IS
'创建时间';

COMMENT ON COLUMN SEC_WF_ROLE.LAST_UPDATED_BY IS
'最后修改人';

COMMENT ON COLUMN SEC_WF_ROLE.LAST_UPDATED_DATE IS
'最后修改时间';

CREATE INDEX INX_WF_ROLE_ORG ON SEC_WF_ROLE (
   ORG_ID ASC
);

CREATE INDEX INX_WF_ROLE_ROLE ON SEC_WF_ROLE (
   ROLE_ID ASC
);

CREATE OR REPLACE VIEW V_SEC_MANAGE_ROLE AS
SELECT * 
FROM "SEC_ROLE"
WHERE "TYPE" IN (0,2)
WITH READ ONLY;

 COMMENT ON TABLE V_SEC_MANAGE_ROLE IS
'所有管理角色';

CREATE OR REPLACE VIEW V_SEC_VALID_ORG AS
SELECT *
FROM
   "SEC_ORGANIZATION"
WHERE
   "STATUS" = 'enabled'

WITH READ ONLY;

 COMMENT ON TABLE V_SEC_VALID_ORG IS
'所有启用的组织机构';

CREATE OR REPLACE VIEW V_SEC_WORKFLOW_ROLE AS
SELECT * 
FROM "SEC_ROLE"
WHERE "TYPE" IN (1,2)
WITH READ ONLY;

 COMMENT ON TABLE V_SEC_WORKFLOW_ROLE IS
'所有流程角色';

